mapper.select:
select * from kmcs_task
where 1=1

    AND task_id =  #{taskId}

mapper.update:
UPDATE kmsc_task
SET task_id = #{taskId},  app_id = #{appId},barcode = #{barcode}, experiment_no = #{experimentNo}
,test_item_code = #{testItemCode},test_item_name = #{testItemName},big_org_code = #{bizOrgCode}
WHERE task_id = #{taskId}

    AND name = #{name}


    AND age = #{age}
